    <script>
        document.addEventListener('DOMContentLoaded', function () {
            var swiper = new Swiper('.swiper', {
                loop: true,
                navigation: {
                    nextEl: '.countries-section-button[aria-label="Next slide"]',
                    prevEl: '.countries-section-button[aria-label="Previous slide"]',
                },
                slidesPerView: 1,
                spaceBetween: 30,
            });

            swiper.on('slideChange', function () {
                console.log('Slide changed');
            });
        });

        // Get all list items in the slider
        const listItems = document.querySelectorAll('.slider_hover__item li');

        // Loop through each list item
        listItems.forEach(item => {
            item.addEventListener('mouseenter', function () {
                // Remove active class from all items
                listItems.forEach(li => li.classList.remove('active'));
                // Add active class to the currently hovered item
                item.classList.add('active');
            });
        });
    </script>
